import { useState } from 'react'

const Square = () => {
  const [value, setValue] = useState(null)

  const handleClick = (value) => {
    value = 'X'
    setValue(value)
  }
  return <button className='square' onClick={handleClick}>{value}</button>
}

export default function Board() {
  return (
    <>
      <div className='board-row'>
        <Square />='1' />
        <Square />='2' />
        <Square />='3' />
      </div>
      <div className='board-row'>
        <Square />='4' />
        <Square />='5' />
        <Square />='6' />
      </div>
      <div className='board-row'>
        <Square />='7' />
        <Square />='8' />
        <Square />='9' />
      </div>
    </>
  )
}
